import logo from './logo.svg';
import './App.css';
import './index.css';

import React, { useState } from 'react';
import { SignupPage, LoginPage } from './LoginAndSignUp';

const App = () => {
  const [currentPage, setCurrentPage] = useState('login');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);

  const handleLoginSuccess = (userData) => {
    setIsAuthenticated(true);
    setUser(userData);
    // Here you could redirect to a dashboard or home page
    console.log('Logged in successfully:', userData);
  };

  const handleSignupSuccess = (userData) => {
    // After successful signup, switch to login page
    setCurrentPage('login');
    console.log('Signup successful:', userData);
  };

  const handleLogout = async () => {
    try {
      const response = await fetch('/auth/logout', {
        method: 'POST',
      });

      if (response.ok) {
        setIsAuthenticated(false);
        setUser(null);
      }
    } catch (error) {
      console.error('Logout failed:', error);
    }
  };

  // If user is authenticated, show a simple logout button
  if (isAuthenticated && user) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="text-center">
          <h1 className="text-2xl font-bold mb-4">Welcome, {user.username}!</h1>
          <button
            onClick={handleLogout}
            className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
          >
            Logout
          </button>
        </div>
      </div>
    );
  }

  // Show either login or signup page
  return currentPage === 'login' ? (
    <LoginPage 
      onSignupClick={() => setCurrentPage('signup')}
      onLoginSuccess={handleLoginSuccess}
    />
  ) : (
    <SignupPage
      onLoginClick={() => setCurrentPage('login')}
      onSignupSuccess={handleSignupSuccess}
    />
  );
};

export default App;
