import { useState, useEffect } from 'react';
import { AlertCircle } from 'lucide-react';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Button } from '@/components/ui/button';
import { Card, CardHeader, CardTitle, CardDescription, CardContent, CardFooter } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';



const SignupPage = ({onLoginClick, onSignupSuccess})=>{
    const [formInfo, setFormInfo] = useState({
        fullName: '', username:'', email: '', password: ''
    });

    const [errors, setErrors] = useState([]);
    const[loading, setLoading] = useState(false);

    const handleChange =(e) =>{
        setFormInfo({...formInfo, [e.target.name]: e.target.value});
        setErrors (null);
    }
    const handleSubmit = async (e) =>{
        e.preventDefault();
        setLoading(true);
        setErrors(null);

        try{
            const response = await fetch('/auth/signup', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formInfo)
              });

            const data = await response.json();
            if (!response.ok){
                setErrors({
                    message: response.data.error,
                    details: response.data.details
                });
                return;
            }

            if (onSignupSuccess){
                onSignupSuccess(data.user);
            }
        }catch(error){
            setErrors({
                message: 'An error occurs, please retry'
            })
        }finally{
            setLoading(false);
        }
    };



    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
          <Card className="w-full max-w-sm">
            <CardHeader>
              <CardTitle className="text-center text-2xl font-bold">Create an Account</CardTitle>
              <CardDescription className="text-center">
                Sign up to join the community
              </CardDescription>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleSubmit} className="space-y-4">
                {errors.length > 0 && (
                  <Alert variant="destructive">
                    <AlertCircle className="h-4 w-4" />
                    <AlertDescription>
                      {errors.map((error, index) => (
                        <div key={index}>{error}</div>
                      ))}
                    </AlertDescription>
                  </Alert>
                )}

                <div className="space-y-2">
                  <Label htmlFor="fullName">Full Name</Label>
                  <Input
                    id="fullName"
                    name="fullName"
                    type="text"
                    required
                    value={formInfo.fullName}
                    onChange={handleChange}
                    className="w-full"
                  />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="username">Username</Label>
                  <Input
                    id="username"
                    name="username"
                    type="text"
                    required
                    value={formInfo.username}
                    onChange={handleChange}
                    className="w-full"
                  />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="email">Email</Label>
                  <Input
                    id="email"
                    name="email"
                    type="email"
                    required
                    value={formInfo.email}
                    onChange={handleChange}
                    className="w-full"
                  />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="password">Password</Label>
                  <Input
                    id="password"
                    name="password"
                    type="password"
                    required
                    value={formInfo.password}
                    onChange={handleChange}
                    className="w-full"
                  />
                </div>

                <Button
                  type="submit"
                  className="w-full"
                  disabled={loading}
                >
                  {loading ? 'Creating account...' : 'Sign up'}
                </Button>
              </form>
            </CardContent>
            <CardFooter className="justify-center">
              <p className="text-sm">
                Already have an account?{' '}
                <Button
                  variant="link"
                  className="p-0"
                  onClick={onLoginClick}
                >
                  Log in
                </Button>
              </p>
            </CardFooter>
          </Card>
        </div>
      );
};

const LoginPage = ({onSignupClick, onLoginSuccess}) =>{
    const[formData, setFormData] = useState({
        username: '', password: ''
    });

    const [error, setError] = useState([]);
    const [loading, setLoading] = useState(false);

    const handleChange = (event) =>{
        setFormData({ ...formData, [event.target.name]: event.target.value });
        setError(null);
    }

    const handleSubmit = async(event) =>{
        event.preventDefault();
        setLoading(true);
        setError(null);

        try{
            const response = await fetch({
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData)
            });

            const data = await response.json();
            if (!response.ok){
                setError({
                    message: data.error,
                    details: data.details 
                });
                return;
            }

            if (onLoginSuccess){
                onLoginSuccess(data.user);
            }

        }catch(error){
            setError({
                message: 'An error occurred. Please try again.'
              });
        }finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
          <Card className="w-full max-w-md">
            <CardHeader>
              <CardTitle className="text-center text-2xl font-bold">Welcome Back</CardTitle>
              <CardDescription className="text-center">
                Log in to your account
              </CardDescription>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleSubmit} className="space-y-4">
                {error && (
                  <DisplayError error={error.message} details={error.details} />
                )}

                <div className="space-y-2">
                  <Label htmlFor="username">Username</Label>
                  <Input
                    id="username"
                    name="username"
                    type="text"
                    required
                    value={formData.username}
                    onChange={handleChange}
                    className="w-full"
                  />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="password">Password</Label>
                  <Input
                    id="password"
                    name="password"
                    type="password"
                    required
                    value={formData.password}
                    onChange={handleChange}
                    className="w-full"
                  />
                </div>

                <Button
                  type="submit"
                  className="w-full"
                  disabled={loading}
                >
                  {loading ? 'Logging in...' : 'Log in'}
                </Button>
              </form>
            </CardContent>
            <CardFooter className="justify-center">
              <p className="text-sm">
                Don't have an account?{' '}
                <Button
                  variant="link"
                  className="p-0"
                  onClick={onSignupClick}
                >
                  Sign up
                </Button>
              </p>
            </CardFooter>
          </Card>
        </div>
      );
    };

export { SignupPage, LoginPage };
