import logo from './logo.svg';
import './App.css';
import './index.css';
import './output.css';
import React, { useState, useEffect } from 'react';
import { SignupPage, LoginPage, LoggedInPage } from './LoginAndSignUp'; 
import  MainPage  from './MainPage';




const App = () => {
  const [currentPage, setCurrentPage] = useState('main'); // 
  const [user, setUser] = useState(null);

  const handleLoginClick = () => {
    setCurrentPage('login');
  };



  const handleSignupClick = () => {
    setCurrentPage('signup');
  };

  const handleSignupSuccess = (userData) => {
    setUser(userData);
    setCurrentPage('main');
  };

  const handleLoginSuccess = (userData) => {
    setUser(userData);
    setCurrentPage('main');
  };

  const handleLogout = () => {
    setUser(null);
    setCurrentPage('main');
  };


  return (
    <div>
      {currentPage ==='main' && (
        <div>
          <div className= "flex justify-end p-4 bg-white shadow-sm">
            {user?(
              <button onClick={handleLogout} className='px-4 py-2 rounded-full bg-gray-800 text-white hover:bg-gray-800'>

                Logout
              </button>
            ):(
              <div className='space-x-4'>
                <button onClick = {handleLoginClick}
                  className = "px-4 rounded-full bg-blue-50 text-yellow-100 hover:bg-blue-600">

                Login
                </button>

              </div>
            )

            }



          </div>

        </div>
      )}

      {currentPage === 'signup' && (
        <SignupPage 
          onLoginClick={handleLoginClick}
          onSignupSuccess={handleSignupSuccess}
        />
      )}

      {currentPage === 'login' && (
        <LoginPage
          onSignupClick={handleSignupClick}
          onLoginSuccess={handleLoginSuccess}
        />
      )}

      {currentPage === 'logged-in' && user && (
        <LoggedInPage 
          user={user}
          onLogout={handleLogout}
        />
      )}
    </div>
  );
};

export default App;
