import React, { useState, useEffect } from 'react';

import './output.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
    faUser, 
    faEnvelope, 
    faLock, 
    faUserCircle, 
} from '@fortawesome/free-solid-svg-icons';
import { faTwitter} from '@fortawesome/free-brands-svg-icons';
import {faThumbsUp, faComment} from '@fortawesome/free-regular-svg-icons';

const MainPage = ({ user = {}, onLogout }) => { 
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newComment, setNewComment] = useState('');


  useEffect(() => {
    fetchPosts();
  }, [user?._id]); 

  const fetchPosts = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/v1/posts/all', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        },
        credentials: 'include'
      });

      if (!response.ok) {
        throw new Error('Failed to fetch posts');
      }

      const allPosts = await response.json();

      if (user?._id){ // we want to check if the user is logged in, if it is, then filter posts that are join the following list id
        const filterPosts = allPosts.filter(post=>{
            return post.user._id ===user._id || user.following.includes(post.user._id); //include either self or following's post
        });
        setPosts(filterPosts);
      }else{
        setPosts(allPosts);
      }

    } catch (error) {
      setError('Failed to load posts. Please try again later.');
      console.error('Error fetching posts:', error);
    } finally {
      setLoading(false);
    }
  };
// helper function to parse the timestamp in mongodb to new date format
  const formatDate = (createAt) => {
    if (!createAt) return '';
    return new Date(createAt).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const handleLikeUnlike = async(postId) =>{
    if (!user?._id){
        setError("Please log in to like or unlike the post"); //can only like or unlike the post when the user is logged in
        return;
    }

    try{
        const response = await fetch(`http://localhost:3001/api/v1/posts/like/${postId}`,{
            method: 'PUT',
            headers: {
                 'Content-Type': 'application/json'
            },
            credentials: 'include'
        });
        if (!response.ok) throw new Error('Failed to like/unlike post');
        const updatedLikes = await response.json();

        setPosts(posts.map(post =>{
            if (post._id==postId){
                return {...post, likes: updatedLikes.likes};
            }
            return post;
        }));


    }catch(error){
        console.error('Error liking/unliking post:', error);
        setError('Failed to like/unlike post');
    }
  };

  const handleComment = async (postId) => {
    if (!user?._id) {
      setError('Please log in to comment');
      return;
    }

    if (!newComment.trim()) {
        setError('Comment cannot be empty');
        return;
      }
      try{
        const response = await fetch(`http://localhost:3001/api/v1/posts/comment/${postId}`, {
            method: 'PATCH',
            headers:{
                'Content-Type': 'application/json'
            },
            credentials: 'include',
            body: JSON.stringify({ comments: newComment })
        });
        if (!response.ok) throw new Error('Failed to add comment');

      setNewComment('');
      fetchPosts();
      }catch(error){
        console.error('Error adding comment:', error);
        setError('Failed to add comment');
      }
  }

  const isLikedByUser = (post) => {
    return post.likes.includes(user?._id);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex items-center">
              <h1 className="text-2xl font-bold text-gray-900">
              <FontAwesomeIcon icon={faUser} />
                {user?._id ? 'Your Feed' : 'Recent Posts From All Users'}
              </h1>
            </div>
            <div className="flex items-center space-x-4">
              {user?._id ? (
                <>
                  <span className="text-gray-700">Welcome, {user.username}!</span>
                  <button
                    onClick={onLogout}
                    className="px-4 py-2 rounded-full bg-gray-800 text-white hover:bg-gray-700"
                  >
                    Logout
                  </button>
                </>
              ) : (
                <div className="text-gray-600">Please log in to interact with posts</div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-3xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
        {loading ? (
          <div className="text-center py-8">
            <div className="text-gray-600">Loading posts...</div>
          </div>
        ) : error ? (
          <div className="bg-red-50 border border-red-200 rounded-md p-4 text-red-700">
            {error}
          </div>
        ) : posts.length === 0 ? (
          <div className="text-center py-8">
            <div className="text-gray-600">
              {user?._id ? "No posts from people you follow yet" : "No posts available"}
            </div>
          </div>
        ) : (
          <div className="space-y-6">
            {posts.map((post) => (
              <div key={post._id} className="bg-white rounded-lg shadow overflow-hidden">
                {/* Post Header */}
                <div className="p-4 border-b">
                  <div className="flex items-center">
                    <img
                      src={post.user.profileImg || "https://picsum.photos/seed/picsum/50/50"}
                      alt={post.user.username}
                      className="h-10 w-10 rounded-full"
                    />
                    <div className="ml-3">
                      <p className="text-sm font-medium text-gray-900">
                        {post.user.fullName}
                      </p>
                      <p className="text-xs text-gray-500">
                        @{post.user.username}
                      </p>
                      <p className="text-xs text-gray-500">
                        {formatDate(post.createdAt)}
                      </p>
                    </div>
                  </div>
                </div>

                {/* Post Content */}
                <div className="p-4">
                  <p className="text-gray-800 whitespace-pre-wrap">{post.text}</p>
                  {post.img && (
                    <img
                      src={post.img}
                      alt="Post attachment"
                      className="mt-3 rounded-lg max-h-96 w-full object-cover"
                    />
                  )}
                </div>

                {/* Post Actions */}
                <div className="px-4 py-3 border-t bg-gray-50">
                  <div className="flex items-center justify-center">
                    <button 
                      onClick={() => handleLikeUnlike(post._id)}
                      disabled={!user?._id}
                      className={`flex items-center space-x-2 ${
                        isLikedByUser(post) 
                          ? 'text-blue-500' 
                          : 'text-gray-500 hover:text-blue-500'
                      } ${!user?._id && 'cursor-not-allowed opacity-50'}`}
                    >
                      <FontAwesomeIcon icon={faThumbsUp} />
                      <span>{post.likes.length}</span>
                    </button>

                    {user?._id && (
                      <div className="flex-1 mx-4">
                        <input
                          type="text"
                          value={newComment}
                          onChange={(e) => setNewComment(e.target.value)}
                          placeholder="Add a comment..."
                          className="w-full px-3 py-1 text-sm border rounded-full focus:outline-none focus:ring-1 focus:ring-blue-500"
                          onKeyPress={(e) => {
                            if (e.key === 'Enter') {
                              handleComment(post._id);
                            }
                          }}
                        />
                      </div>
                    )}

                    <div className="flex items-center space-x-2 text-gray-500">
                      <FontAwesomeIcon icon={faComment} />
                      <span>{post.comments.length}</span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default MainPage;
