import React, { useState, useEffect } from 'react';
import { AlertCircle } from 'lucide-react';


const DisplayError = ({ error, details }) => (
    <div className="bg-red-50 border-l-4 border-red-500 p-4 text-red-700">
      <p>{error}</p>
      {details && details.map((detail, index) => (
        <p key={index} className="text-sm mt-1">{detail}</p>
      ))}
    </div>
  );

const SignupPage = ({onLoginClick, onSignupSuccess})=>{
    const [formInfo, setFormInfo] = useState({
        fullName: '', username:'', email: '', password: ''
    });

    const [error, setError] = useState([]);
    const[loading, setLoading] = useState(false);

    const handleChange =(e) =>{
        setFormInfo({...formInfo, [e.target.name]: e.target.value});
        setError (null);
    }
    const handleSubmit = async (e) =>{
        e.preventDefault();
        setLoading(true);
        setError(null);

        try{
            const response = await fetch('/auth/signup', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formInfo)
              });

            const data = await response.json();
            if (!response.ok){
                setError({
                    message: response.data.error,
                    details: response.data.details
                });
                return;
            }

            if (onSignupSuccess){
                onSignupSuccess(data.user);
            }
        }catch(error){
            setError({
                message: 'An error occurs, please retry'
            })
        }finally{
            setLoading(false);
        }
    };


    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4">
          <div className="max-w-md w-full space-y-8 bg-white p-8 rounded-lg shadow">
            <div>
              <h2 className="text-center text-3xl font-bold">Create an Account</h2>
              <p className="text-center text-gray-600 mt-2">
                Sign up to join the community
              </p>
            </div>

            <form onSubmit={handleSubmit} className="mt-8 space-y-6">
              {error && <DisplayError error={error.message} details={error.details} />}

              <div>
                <label htmlFor="fullName" className="block text-sm font-medium text-gray-700">
                  Full Name
                </label>
                <input
                  id="fullName"
                  name="fullName"
                  type="text"
                  required
                  value={formInfo.fullName}
                  onChange={handleChange}
                  className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                />
              </div>



              <button
                type="submit"
                disabled={loading}
                className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
              >
                {loading ? 'Creating account...' : 'Sign up'}
              </button>
            </form>

            <div className="text-center mt-4">
              <p className="text-sm text-gray-600">
                Already have an account?{' '}
                <button
                  onClick={onLoginClick}
                  className="font-medium text-blue-600 hover:text-blue-500"
                >
                  Log in
                </button>
              </p>
            </div>
          </div>
        </div>
      );
    };



const LoginPage = ({onSignupClick, onLoginSuccess}) =>{
    const[formData, setFormData] = useState({
        username: '', password: ''
    });

    const [error, setError] = useState([]);
    const [loading, setLoading] = useState(false);

    const handleChange = (event) =>{
        setFormData({ ...formData, [event.target.name]: event.target.value });
        setError(null);
    }

    const handleSubmit = async(event) =>{
        event.preventDefault();
        setLoading(true);
        setError(null);

        try{
            const response = await fetch({
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData)
            });

            const data = await response.json();
            if (!response.ok){
                setError({
                    message: data.error,
                    details: data.details 
                });
                return;
            }

            if (onLoginSuccess){
                onLoginSuccess(data.user);
            }

        }catch(error){
            setError({
                message: 'An error occurred. Please try again.'
              });
        }finally {
            setLoading(false);
        }
    };
    return (
        <div className="grid grid-cols-1 md:grid-cols-2 h-screen">
          {/* Left side - Blue background with Twitter logo */}
          <div className="hidden md:flex bg-blue-500 items-center justify-center">
            <svg
              viewBox="0 0 24 24"
              className="w-96 h-96 text-white"
              fill="currentColor"
            >
              <path d="M23.643 4.937c-.835.37-1.732.62-2.675.733.962-.576 1.7-1.49 2.048-2.578-.9.534-1.897.922-2.958 1.13-.85-.904-2.06-1.47-3.4-1.47-2.572 0-4.658 2.086-4.658 4.66 0 .364.042.718.12 1.06-3.873-.195-7.304-2.05-9.602-4.868-.4.69-.63 1.49-.63 2.342 0 1.616.823 3.043 2.072 3.878-.764-.025-1.482-.234-2.11-.583v.06c0 2.257 1.605 4.14 3.737 4.568-.392.106-.803.162-1.227.162-.3 0-.593-.028-.877-.082.593 1.85 2.313 3.198 4.352 3.234-1.595 1.25-3.604 1.995-5.786 1.995-.376 0-.747-.022-1.112-.065 2.062 1.323 4.51 2.093 7.14 2.093 8.57 0 13.255-7.098 13.255-13.254 0-.2-.005-.402-.014-.602.91-.658 1.7-1.477 2.323-2.41z" />
            </svg>
          </div>

          {/* Right side - Sign up form */}
          <div className="flex flex-col justify-center px-8 md:px-16">
            {/* Mobile logo */}
            <div className="md:hidden mb-8">
              <svg
                viewBox="0 0 24 24"
                className="w-10 h-10 text-blue-500"
                fill="currentColor"
              >
                <path d="M23.643 4.937c-.835.37-1.732.62-2.675.733.962-.576 1.7-1.49 2.048-2.578-.9.534-1.897.922-2.958 1.13-.85-.904-2.06-1.47-3.4-1.47-2.572 0-4.658 2.086-4.658 4.66 0 .364.042.718.12 1.06-3.873-.195-7.304-2.05-9.602-4.868-.4.69-.63 1.49-.63 2.342 0 1.616.823 3.043 2.072 3.878-.764-.025-1.482-.234-2.11-.583v.06c0 2.257 1.605 4.14 3.737 4.568-.392.106-.803.162-1.227.162-.3 0-.593-.028-.877-.082.593 1.85 2.313 3.198 4.352 3.234-1.595 1.25-3.604 1.995-5.786 1.995-.376 0-.747-.022-1.112-.065 2.062 1.323 4.51 2.093 7.14 2.093 8.57 0 13.255-7.098 13.255-13.254 0-.2-.005-.402-.014-.602.91-.658 1.7-1.477 2.323-2.41z" />
              </svg>
            </div>

            <div className="mb-8">
              <h1 className="text-6xl font-bold mb-8">Happening now</h1>
              <h2 className="text-3xl font-bold">Join today.</h2>
            </div>

            {error && (
              <div className="mb-4 p-4 bg-red-50 border-l-4 border-red-500 text-red-700">
                <p>{error.message}</p>
                {error.details && error.details.map((detail, index) => (
                  <p key={index} className="text-sm mt-1">{detail}</p>
                ))}
              </div>
            )}

            <form onSubmit={handleSubmit} className="space-y-4 max-w-sm">
              <div>
                <input
                  id="fullName"
                  name="fullName"
                  type="text"
                  required
                  placeholder="Full name"
                  value={formInfo.fullName}
                  onChange={handleChange}
                  className="w-full px-4 py-3 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>

              <div>
                <input
                  id="username"
                  name="username"
                  type="text"
                  required
                  placeholder="Username"
                  value={formInfo.username}
                  onChange={handleChange}
                  className="w-full px-4 py-3 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>

              <div>
                <input
                  id="email"
                  name="email"
                  type="email"
                  required
                  placeholder="Email"
                  value={formInfo.email}
                  onChange={handleChange}
                  className="w-full px-4 py-3 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>

              <div>
                <input
                  id="password"
                  name="password"
                  type="password"
                  required
                  placeholder="Password"
                  value={formInfo.password}
                  onChange={handleChange}
                  className="w-full px-4 py-3 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>

              <button
                type="submit"
                disabled={loading}
                className="w-full py-3 rounded-full bg-black text-white font-bold hover:bg-gray-900 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-900 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {loading ? 'Creating account...' : 'Sign up'}
              </button>
            </form>

            <div className="mt-8">
              <p className="text-sm text-gray-600">
                Already have an account?{' '}
                <button
                  onClick={onLoginClick}
                  className="text-blue-500 hover:underline"
                >
                  Log in
                </button>
              </p>
            </div>
          </div>
        </div>
      );
    };



export { SignupPage, LoginPage };
