import logo from './logo.svg';
import './App.css';
import './index.css';
import './output.css';
import React, { useState, useEffect } from 'react';
import { SignupPage, LoginPage, LoggedInPage } from './LoginAndSignUp'; 
import  MainPage  from './MainPage';
import CreatePostForm from './CreatePost';

// const App =() =>{
//  return(<MainPage/>);
// };


const App = () => {
  const [currentPage, setCurrentPage] = useState('main'); // 
  const [user, setUser] = useState(null);

  const handleLoginClick = () => {
    setCurrentPage('login');
  };



  const handleSignupClick = () => {
    setCurrentPage('signup');
  };

  const handleSignupSuccess = (userData) => {
    setUser(userData);
    setCurrentPage('main');
  };

  const handleLoginSuccess = (userData) => {
    setUser(userData);
    setCurrentPage('main');
  };

  const handleLogout = () => {
    setUser(null);
    setCurrentPage('main');
  };


  return (
    <div>
      {currentPage ==='main' && (
        <div>
          <div className= "flex justify-end p-4 bg-white shadow-sm">
            {user?(
              <CreatePostForm/> 

            ):(
              <div className='space-x-4'>
                <button onClick = {handleLoginClick}
                  className = "px-4 py-2 rounded-full bg-gray-800 text-yellow-100 hover:bg-blue-600">

                Login
                </button>
                <button onClick ={handleSignupClick} className = "px-4 py-2 rounded-full bg-gray-800 text-white hover:bg-gray-800 hover:bg-blue-600">

                Sign Up
                </button>

              </div>
            )

            }



          </div>
              <MainPage user ={user} onLogout={ handleLogout}/>
        </div>
      )}

      {currentPage === 'signup' && (
        <div>
          <div className= "flex justify-end p-4 bg-white shadow-sm">
            {user?(
              <button onClick={handleLogout} className='px-4 py-2 rounded-full bg-gray-800 text-white hover:bg-gray-800'>

                Logout
              </button> 

            ):(
              <div className='space-x-4'>
                <button onClick = {handleLoginClick}
                  className = "px-4 py-2 rounded-full bg-gray-800 text-yellow-100 hover:bg-blue-600">

                Login
                </button>
                <button onClick ={handleSignupClick} className = "px-4 py-2 rounded-full bg-gray-800 text-white hover:bg-gray-800 hover:bg-blue-600">

                Sign Up
                </button>

              </div>
            )
            }
          </div>
          <SignupPage 
          onLoginClick={handleLoginClick}
          onSignupSuccess={handleSignupSuccess}
        />
        </div>
      )}

      {currentPage === 'login' && (
        <div>
        <div className= "flex justify-end p-4 bg-white shadow-sm">
          {user?(
            <button onClick={handleLogout} className='px-4 py-2 rounded-full bg-gray-800 text-white hover:bg-gray-800'>

              Logout
            </button> 

          ):(
            <div className='space-x-4'>
              <button onClick = {handleLoginClick}
                className = "px-4 py-2 rounded-full bg-gray-800 text-yellow-100 hover:bg-blue-600">

              Login
              </button>
              <button onClick ={handleSignupClick} className = "px-4 py-2 rounded-full bg-gray-800 text-white hover:bg-gray-800 hover:bg-blue-600">

              Sign Up
              </button>

            </div>
          )
          }
        </div>
        <LoginPage 
        onSignupClick={handleSignupClick}
        onLoginSuccess={handleLoginSuccess}
      />
      </div>
    )}
    </div>
  );
};

export default App;
