import React, { useState, useEffect } from 'react';
import { AlertCircle } from 'lucide-react';


const DisplayError = ({ error, details }) => (
    <div className="bg-red-50 border-l-4 border-red-500 p-4 text-red-700">
      <p>{error}</p>
      {details && details.map((detail, index) => (
        <p key={index} className="text-sm mt-1">{detail}</p>
      ))}
    </div>
  );

const SignupPage = ({onLoginClick, onSignupSuccess})=>{
    const [formInfo, setFormInfo] = useState({
        fullName: '', username:'', email: '', password: ''
    });

    const [error, setError] = useState([]);
    const[loading, setLoading] = useState(false);

    const handleChange =(e) =>{
        setFormInfo({...formInfo, [e.target.name]: e.target.value});
        setError (null);
    }
    const handleSubmit = async (e) =>{
        e.preventDefault();
        setLoading(true);
        setError(null);

        try{
            const response = await fetch('/auth/signup', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formInfo)
              });

            const data = await response.json();
            if (!response.ok){
                setError({
                    message: response.data.error,
                    details: response.data.details
                });
                return;
            }

            if (onSignupSuccess){
                onSignupSuccess(data.user);
            }
        }catch(error){
            setError({
                message: 'An error occurs, please retry'
            })
        }finally{
            setLoading(false);
        }
    };


    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4">
          <div className="max-w-md w-full space-y-8 bg-white p-8 rounded-lg shadow">
            <div>
              <h2 className="text-center text-3xl font-bold">Create an Account</h2>
              <p className="text-center text-gray-600 mt-2">
                Sign up to join the community
              </p>
            </div>

            <form onSubmit={handleSubmit} className="mt-8 space-y-6">
              {error && <DisplayError error={error.message} details={error.details} />}

              <div>
                <label htmlFor="fullName" className="block text-sm font-medium text-gray-700">
                  Full Name
                </label>
                <input
                  id="fullName"
                  name="fullName"
                  type="text"
                  required
                  value={formInfo.fullName}
                  onChange={handleChange}
                  className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                />
              </div>



              <button
                type="submit"
                disabled={loading}
                className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
              >
                {loading ? 'Creating account...' : 'Sign up'}
              </button>
            </form>

            <div className="text-center mt-4">
              <p className="text-sm text-gray-600">
                Already have an account?{' '}
                <button
                  onClick={onLoginClick}
                  className="font-medium text-blue-600 hover:text-blue-500"
                >
                  Log in
                </button>
              </p>
            </div>
          </div>
        </div>
      );
    };



const LoginPage = ({onSignupClick, onLoginSuccess}) =>{
    const[formData, setFormData] = useState({
        username: '', password: ''
    });

    const [error, setError] = useState([]);
    const [loading, setLoading] = useState(false);

    const handleChange = (event) =>{
        setFormData({ ...formData, [event.target.name]: event.target.value });
        setError(null);
    }

    const handleSubmit = async(event) =>{
        event.preventDefault();
        setLoading(true);
        setError(null);

        try{
            const response = await fetch({
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData)
            });

            const data = await response.json();
            if (!response.ok){
                setError({
                    message: data.error,
                    details: data.details 
                });
                return;
            }

            if (onLoginSuccess){
                onLoginSuccess(data.user);
            }

        }catch(error){
            setError({
                message: 'An error occurred. Please try again.'
              });
        }finally {
            setLoading(false);
        }
    };
    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-50 px-4 py-12">
          <div className="w-full max-w-md">
            {/* Card Container */}
            <div className="bg-white px-6 py-8 rounded-lg shadow-md">
              {/* Header */}
              <div className="text-center mb-8">
                <h2 className="text-2xl font-bold text-gray-900">Welcome Back</h2>
                <p className="text-sm text-gray-600 mt-2">
                  Log in to your account
                </p>
              </div>

              {/* Error Display */}
              {error && (
                <div className="mb-4 bg-red-50 border border-red-400 text-red-700 px-4 py-3 rounded relative">
                  <p className="font-medium">{error.message}</p>
                  {error.details && error.details.map((detail, index) => (
                    <p key={index} className="text-sm">{detail}</p>
                  ))}
                </div>
              )}

              {/* Form */}
              <form onSubmit={handleSubmit} className="space-y-6">
                {/* Username Field */}
                <div>
                  <label 
                    htmlFor="username" 
                    className="block text-sm font-medium text-gray-700 mb-1"
                  >
                    Username
                  </label>
                  <input
                    id="username"
                    name="username"
                    type="text"
                    required
                    value={formData.username}
                    onChange={handleChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md 
                             focus:outline-none focus:ring-2 focus:ring-blue-500 
                             focus:border-blue-500"
                    placeholder="Enter your username"
                  />
                </div>

                {/* Password Field */}
                <div>
                  <label 
                    htmlFor="password" 
                    className="block text-sm font-medium text-gray-700 mb-1"
                  >
                    Password
                  </label>
                  <input
                    id="password"
                    name="password"
                    type="password"
                    required
                    value={formData.password}
                    onChange={handleChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md 
                             focus:outline-none focus:ring-2 focus:ring-blue-500 
                             focus:border-blue-500"
                    placeholder="Enter your password"
                  />
                </div>

                {/* Submit Button */}
                <button
                  type="submit"
                  disabled={loading}
                  className="w-full bg-blue-600 text-white py-2 px-4 rounded-md
                           font-medium hover:bg-blue-700 focus:outline-none 
                           focus:ring-2 focus:ring-blue-500 focus:ring-offset-2
                           disabled:opacity-50 disabled:cursor-not-allowed
                           transition duration-200"
                >
                  {loading ? 'Logging in...' : 'Log in'}
                </button>
              </form>

              {/* Footer */}
              <div className="mt-6 text-center">
                <p className="text-sm text-gray-600">
                  Don't have an account?{' '}
                  <button
                    onClick={onSignupClick}
                    className="text-blue-600 hover:text-blue-700 font-medium
                             focus:outline-none focus:underline"
                  >
                    Sign up
                  </button>
                </p>
              </div>
            </div>
          </div>
        </div>
      );
    };



export { SignupPage, LoginPage };
